mapper.update:
update goods
set boxId = #{boxId}, goodsName = #{goodsName}
where id = #{id}

mapper.select:
SELECT t1.id as id, t2.boxName as boxName, t1.goodsName as goodsName, t3.address as address FROM goods t1 LEFT JOIN box t2 ON t1.boxid = t2.id LEFT JOIN area t3 ON t2.areaId = t3.id
and t3.id = #{areaId}
and t2.id = #{boxId}
and t1.goodsName like concat('%', #{goodsName}, '%')
